import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Row } from "react-bootstrap";
import Button from "../button/Button";

import Card from "../card/Card";

import { buscarPersonajes } from "../../actions/persActions";

import "./cards.scss";

const Cards = () => {
	const dispatch = useDispatch();
	const { pers } = useSelector((state) => state);
	const { pers: characters, error, loading } = pers;

	const [filtro, setFiltro] = useState(0);

	useEffect(() => {
		dispatch(buscarPersonajes(filtro));
	}, [filtro]);

	return (
		<>
			<section>
				<div className="buttons-container">
					<Button indice={1} setFiltro={setFiltro}>
						Estudiantes
					</Button>
					<Button indice={2} setFiltro={setFiltro}>
						Staff
					</Button>
				</div>
			</section>
			<main>
				{loading ? (
					"Cargando..."
				) : (
					<Row>
						{characters.map((el) => (
							<Card key={el.name} person={el} />
						))}
					</Row>
				)}
				{error && <p>{error}</p>}
			</main>
		</>
	);
};

export default Cards;
