import { BUSCAR_PERS, BUSCAR_PERS_ERROR, BUSCAR_PERS_SUCCESS } from "../types";

//Buscar personajes
export function buscarPersonajes(index) {
	return (dispatch) => {
		dispatch(fetchAllCharacteres());
		let apiStr;
		try {
			//Todos
			switch (index) {
				case 1:
					apiStr = "http://hp-api.herokuapp.com/api/characters/students";
					break;
				case 2:
					apiStr = "http://hp-api.herokuapp.com/api/characters/staff";
					break;
				default:
					apiStr = "http://hp-api.herokuapp.com/api/characters";
					break;
			}
			fetch(apiStr)
				.then((response) => response.json())
				.then((data) => {
					dispatch(fetchAllCharacteresSuccess(data));
					return;
				})
				.catch((e) =>
					dispatch(
						fetchAllCharacteresError(
							"Error al obtener información de los personajes."
						)
					)
				);
		} catch (error) {
			dispatch(fetchAllCharacteresError(error));
		}
	};
}

const fetchAllCharacteres = () => ({
	type: BUSCAR_PERS,
});

const fetchAllCharacteresSuccess = (data) => ({
	type: BUSCAR_PERS_SUCCESS,
	payload: data,
});
const fetchAllCharacteresError = (error) => ({
	type: BUSCAR_PERS_ERROR,
	payload: error,
});
