import { BUSCAR_PERS, BUSCAR_PERS_ERROR, BUSCAR_PERS_SUCCESS } from "../types";

//Buscar personajes
export function buscarPersonajes() {
	return (dispatch) => {
		dispatch(fetchAllCharacteres());

		try {
			fetch("http://hp-api.herokuapp.com/api/characters")
				.then((response) => {
					if (!response.ok) throw new Error(response.status);
					return response.json();
				})
				.then((data) => {
					dispatch(fetchAllCharacteresSuccess(data));
					return;
				})
				.catch((e) =>
					console.error("Error al obtener información de los movimientos.")
				);
		} catch (error) {
			dispatch(fetchAllCharacteresError());
		}
	};
}

const fetchAllCharacteres = () => ({
	type: BUSCAR_PERS,
});

const fetchAllCharacteresSuccess = (data) => ({
	type: BUSCAR_PERS_SUCCESS,
	payload: data
});
const fetchAllCharacteresError = () => ({
	type: BUSCAR_PERS_ERROR,
});
