import {
	BUSCAR_PERS,
	BUSCAR_PERS_ERROR,
	BUSCAR_PERS_SUCCESS,
	SET_FAVORITE,
	REMOVE_FAVORITE,
	ADD_CHARACTER
} from "../types";

//state
const initialState = {
	pers: [],
	fav: [],
	favcount: 0,
	error: null,
	loading: false,
};

export default function (state = initialState, action) {
	switch (action.type) {
		case BUSCAR_PERS:
			return {
				...state,
				loading: true,
			};
		case BUSCAR_PERS_SUCCESS:
			return {
				...state,
				pers: action.payload,
				loading: false,
			};
		case BUSCAR_PERS_ERROR:
			return {
				...state,
				error: action.payload,
				loading: false,
			};
		case SET_FAVORITE:
			return {
				...state,
				pers: state.pers.map((per, i) =>
					i === action.payload.id
						? { ...per, favorite: action.payload.value }
						: per
				),
				favcount: action.payload.value
					? state.favcount + 1
					: state.favcount - 1,
			};
		case REMOVE_FAVORITE:
			return {
				...state,
				pers: state.pers.map((per, i) =>
					i === action.payload ? { ...per, favorite: false } : per
				),
				favcount: state.favcount - 1
			};
		default:
			return state;
	}
}
