import React from "react";
import { Row, Col } from "react-bootstrap";

import "./card.scss";

import favicon from "../../assets/Rectangle1.png";

const Card = ({ person }) => {
	const {
		name,
		gender,
		dateOfBirth,
		eyeColour,
		hairColour,
		alive,
		house,
		hogwartsStudent,
		image
	} = person;
	return (
		<Col xs={6}>
			<div className="hp-card">
				<Row>
					<Col lg={5} className="card-img slytherine">
						<div className="card-img-container">
							<img
								className="main-pic"
								src={image}
								alt="Character"
							/>
						</div>
					</Col>
					<Col lg={7} className="card-body">
						<img className="fav-icon" src={favicon} alt="Favorito" />
						<p className="top">
							<span>{alive?"Vivo":"Finado"}</span>
							<span>{hogwartsStudent?"Estudiante":"Staff"}</span>
						</p>
						<h3>{name}</h3>
						<p>
							<strong>Cumpleaños:</strong> {dateOfBirth}
						</p>
						<p>
							<strong>Género:</strong> {gender}
						</p>
						<p>
							<strong>Color de ojos:</strong> {eyeColour}
						</p>
						<p>
							<strong>Color de pelo:</strong> {hairColour}
						</p>
					</Col>
				</Row>
			</div>
		</Col>
	);
};

export default Card;
