import { BUSCAR_PERS, BUSCAR_PERS_ERROR, BUSCAR_PERS_SUCCESS } from "../types";

//state
const initialState = {
	pers: [],
	fav: [],
	error: null,
	loading: false,
};

export default function (state = initialState, action) {
	switch (action.type) {
		case BUSCAR_PERS:
			return {
				...state,
				loading: true,
			};
		case BUSCAR_PERS_SUCCESS:
			return {
				...state,
				pers: action.payload,
				loading: false,
			};
		case BUSCAR_PERS_ERROR:
			return {
				...state,
				error: action.payload,
				loading: false,
			};
		default:
			return state;
	}
}
