import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Row } from "react-bootstrap";
import Button from "../button/Button";

import Card from "../card/Card";

import { buscarPersonajes } from "../../actions/persActions";

import "./cards.scss";

const Cards = () => {
	const dispatch = useDispatch();
	const { pers } = useSelector((state) => state);
	const { pers: characters, error, loading } = pers;

	const [filter, setFilter] = useState(0);

	useEffect(() => {
		dispatch(buscarPersonajes());
	}, []);

	return (
		<>
			<section>
				<div className="buttons-container">
					<Button onClickFn={() => setFilter(filter === 0 ? 1 : 0)}>
						Estudiantes
					</Button>
					<Button onClickFn={() => setFilter(filter === 0 ? 2 : 0)}>
						Staff
					</Button>
				</div>
			</section>
			<main>
				{loading ? (
					"Cargando..."
				) : (
					<Row>
						{characters.map((el) => {
							if (filter === 1)
								return el.hogwartsStudent ? (
									<Card key={el.name} person={el} />
								) : null;
							else if (filter === 2)
								return !el.hogwartsStudent ? (
									<Card key={el.id} person={el} />
								) : null;
							else return <Card key={el.name} person={el} />;
						})}
					</Row>
				)}
				{error && <p>{error}</p>}
			</main>
		</>
	);
};

export default Cards;
