import React from "react";
import { Dropdown } from "react-bootstrap";
import BarButton from "../barbutton/BarButton";
import FavItem from "../favitem/FavItem";
import { useSelector } from "react-redux";

import "./bar.scss";

import favicon from "../../assets/Bookmark_fill.png";
import addicon from "../../assets/User_fill_add.png";

const Bar = () => {
	const { pers } = useSelector((state) => state);
	const { pers: favorites } = pers;
	return (
		<div className="bar-container">
			<Dropdown>
				<Dropdown.Toggle>
					<BarButton>
						Favoritos
						<img src={favicon} alt="Favorito" />
					</BarButton>
				</Dropdown.Toggle>
				<Dropdown.Menu>
					{favorites.map((fav) =>
						fav.favorite ? <FavItem item={fav} /> : null
					)}
				</Dropdown.Menu>
			</Dropdown>
			<BarButton>
				Agregar
				<img src={addicon} alt="Agregar" />
			</BarButton>
		</div>
	);
};

export default Bar;
