import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Row } from "react-bootstrap";
import Button from "../button/Button";

import Card from "../card/Card";

import { buscarPersonajes } from "../../actions/persActions";

import "./cards.scss";

const Cards = () => {
	const dispatch = useDispatch();
	const { pers } = useSelector((state) => state);
	const { pers: characters, error, loading } = pers;

	useEffect(() => {
		dispatch(buscarPersonajes());
	}, []);

	const filterPers = (flag) => {
		alert(flag);
	};

	return (
		<>
			<section>
				<div className="buttons-container">
					<Button onClickFn={() => filterPers(true)}>Estudiantes</Button>
					<Button>Staff</Button>
				</div>
			</section>
			<main>
				{loading ? (
					"Cargando..."
				) : (
					<Row>
						{characters.map((el) => (
							<Card key={el.name} person={el} />
						))}
					</Row>
				)}
				{error && <p>{error}</p>}
			</main>
		</>
	);
};

export default Cards;
